mapper.select:
select a.*,b.fid as 'ffid', b.mid,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.taxAmt,b.taxAmtNo,fcessAmt,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName' from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_supplier c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid where a.billtype = '采购入库'
and (a.billNo like '%${str}%' or c.`name` like '%${str}%' or b.itemCode like '%${str}%' or b.itemModel like '%${str}%')
order by a.billDate desc,a.billNo desc,b.entryId limit #{startpage},#{pagesize}

select a.*,b.fid as 'ffid', b.mid,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.taxAmt,b.taxAmtNo,fcessAmt,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName' from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_customer c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid where a.billtype = '销售出库'
and (a.billNo like '%${str}%' or c.`name` like '%${str}%' or b.itemCode like '%${str}%' or b.itemModel like '%${str}%')
order by a.billDate desc,a.billNo desc,b.entryId limit #{startpage},#{pagesize}

select a.*,b.fid as 'ffid', b.mid,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.taxAmt,b.taxAmtNo,fcessAmt,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName' from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_supplier c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid where a.billtype = #{data.billType} and a.billNo like '%${data.billNo}%' and b.custItemCode like '%${data.mid.custItemCode}%' and b.custItemModel like '%${data.mid.custItemModel}%' and b.itemCode like '%${data.mid.itemCode}%' and b.itemModel like '%${data.mid.itemModel}%'
and a.suppId = #{data.suppId}
and a.depaId = #{data.depaId}
and a.billStaf = #{data.billStaf}
and a.billDate between #{date_start} and #{date_end}

select count(*) from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_customer c on a.suppId = c.fid LEFT JOIN t_staff g on a.billStaf = g.fid left join t_store st on b.stockId = st.fid left join t_saleorderentry sa on b.sourFid = sa.mid and b.sourEntryId = sa.entryId and b.itemId = sa.itemId left join t_saleorder so on so.fid = sa.mid LEFT JOIN ( select sourFid,sourEntryId,itemId,sum(saleOutQty) 'saleOutQty' from t_dz GROUP BY sourFid,sourEntryId,itemId ) i on b.mid = i.sourFid and b.entryId = i.sourEntryId and b.itemId = i.itemId where a.billType = '销售出库'
and a.billDate between #{date_start} and #{date_end}
and a.suppId = #{suppId}
and (a.billNo like '%${cnm}%' or b.itemname like '%${cnm}%' or b.itemcode like '%${cnm}%' or b.itemmodel like '%${cnm}%')
and b.qty-IfNULL(i.saleOutQty,0) > 0
and b.qty-i.saleOutQty<=0

select a.*,b.fid as 'ffid', b.mid,b.entryid,b.itemId,b.itemcode,b.itemname,b.itemmodel,b.custitemcode,b.custitemmodel ,b.qty,b.stockId,b.unitName,b.custOrderNum,b.batchNumber,b.taxPrice,b.taxPriceNo,b.fcess,b.taxAmt,b.taxAmtNo,b.fcessAmt, b.rowRemark,c.`name` as 'suppIdName',sa.qty as 'soQty',i.saleOutQty as 'ydzQty',st.`name` as 'stockName',so.billNo as 'so_billNo' from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_customer c on a.suppId = c.fid LEFT JOIN t_staff g on a.billStaf = g.fid left join t_store st on b.stockId = st.fid left join t_saleorderentry sa on b.sourFid = sa.mid and b.sourEntryId = sa.entryId and b.itemId = sa.itemId left join t_saleorder so on so.fid = sa.mid LEFT JOIN ( select sourFid,sourEntryId,itemId,sum(saleOutQty) 'saleOutQty' from t_dz GROUP BY sourFid,sourEntryId,itemId ) i on b.mid = i.sourFid and b.entryId = i.sourEntryId and b.itemId = i.itemId where a.billType = '销售出库'
and a.billDate between #{date_start} and #{date_end}
and a.suppId = #{suppId}
and (a.billNo like '%${cnm}%' or b.itemname like '%${cnm}%' or b.itemcode like '%${cnm}%' or b.itemmodel like '%${cnm}%')
and b.qty-IfNULL(i.saleOutQty,0) > 0
and b.qty-i.saleOutQty<=0
order by b.mid desc,b.entryid asc limit #{startpage},#{pagesize}

