select count(*) from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_customer c on a.suppId = c.fid LEFT JOIN t_staff g on a.billStaf = g.fid left join t_store st on b.stockId = st.fid left join t_saleorderentry sa on b.sourFid = sa.mid and b.sourEntryId = sa.entryId and b.itemId = sa.itemId left join t_saleorder so on so.fid = sa.mid LEFT JOIN ( select billType,sourFid,sourEntryId,itemId,sum(hxAmt) 'hxAmt' from t_sfk where billType = '收款单' GROUP BY billType,sourFid,sourEntryId,itemId ) i on b.mid = i.sourFid and b.entryId = i.sourEntryId and b.itemId = i.itemId and i.billType = '收款单' where a.billType = '销售出库'
and a.billDate between #{date_start} and #{date_end}
and a.suppId = #{suppId}
and (a.billNo like '%${cnm}%' or b.itemname like '%${cnm}%' or b.itemcode like '%${cnm}%' or b.itemmodel like '%${cnm}%')
and b.taxAmt-IfNULL(i.hxAmt,0) > 0
and b.taxAmt-i.hxAmt<=0

select a.*,b.fid as 'ffid', b.mid,b.entryid,b.itemId,b.itemcode,b.itemname,b.itemmodel,b.custitemcode,b.custitemmodel ,b.qty,b.stockId,b.unitName,b.custOrderNum,b.batchNumber,b.taxPrice,b.taxPriceNo,b.fcess,b.taxAmt,b.taxAmtNo,b.fcessAmt, b.rowRemark,c.`name` as 'suppIdName',sa.qty as 'soQty',st.`name` as 'stockName',so.billNo as 'so_billNo',i.hxAmt from icstockbill a LEFT JOIN icstockbillentry b on a.fid = b.mid LEFT JOIN t_customer c on a.suppId = c.fid LEFT JOIN t_staff g on a.billStaf = g.fid left join t_store st on b.stockId = st.fid left join t_saleorderentry sa on b.sourFid = sa.mid and b.sourEntryId = sa.entryId and b.itemId = sa.itemId left join t_saleorder so on so.fid = sa.mid LEFT JOIN ( select billType,sourFid,sourEntryId,itemId,sum(hxAmt) 'hxAmt' from t_sfk where billType = '收款单' GROUP BY billType,sourFid,sourEntryId,itemId ) i on b.mid = i.sourFid and b.entryId = i.sourEntryId and b.itemId = i.itemId and i.billType = '收款单' where a.billType = '销售出库'
and a.billDate between #{date_start} and #{date_end}
and a.suppId = #{suppId}
and (a.billNo like '%${cnm}%' or b.itemname like '%${cnm}%' or b.itemcode like '%${cnm}%' or b.itemmodel like '%${cnm}%')
and b.taxAmt-IfNULL(i.hxAmt,0) > 0
and b.taxAmt-i.hxAmt<=0
order by b.mid desc,b.entryid asc limit #{startpage},#{pagesize}
