mapper.select:
select a.*,b.fid as 'ffid', b.mid,b.entryId,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.finishDate,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName',i.qty as 'icsQty', h.defaultStorehouseId as 'stockId', h.name as 'stockName' from t_poorder a LEFT JOIN t_poorderentry b on a.fid = b.mid LEFT JOIN t_supplier c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid LEFT JOIN icstockbillentry i on b.mid = i.sourFid and b.entryId = i.sourEntryId and b.itemId = i.itemId left join ( select a.fid,a.defaultStorehouseId,b.`name` from t_item a left join t_store b on a.defaultStorehouseId = b.fid ) h on b.itemId = h.fid where 1=1
and a.suppId = #{suppId}
and (a.billNo like '%${cnm}%' or d.name like '%${cnm}%' or c.`name` like '%${cnm}%' or b.itemName like '%${cnm}%' or b.itemCode like '%${cnm}%' or b.itemModel like '%${cnm}%')
and b.qty-IfNULL(i.qty,0) > 0
and b.qty-i.qty<=0
limit #{startpage},#{pagesize}

select a.*,b.fid as 'ffid', b.mid,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.finishDate,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName' from t_poorder a LEFT JOIN t_poorderentry b on a.fid = b.mid LEFT JOIN t_supplier c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid
where (a.billNo like '%${str}%' or c.`name` like '%${str}%' or b.itemCode like '%${str}%' or b.itemModel like '%${str}%')
order by a.billDate desc,a.billNo desc,b.entryId limit #{startpage},#{pagesize}

select a.*,b.fid as 'ffid', b.mid,b.itemId,b.itemCode,b.itemName,b.itemModel,b.custItemCode,b.custItemModel,b.unitName,b.custOrderNum,b.finishDate,b.qty,b.batchNumber, b.taxPrice,b.taxPriceNo,b.rowRemark,b.sourFid,b.sourBillNo,b.sourEntryId,b.sourType, c.`name` as 'suppIdName' ,d.`name` as 'settIdName',e.name as 'depaIdName' from t_poorder a LEFT JOIN t_poorderentry b on a.fid = b.mid LEFT JOIN t_supplier c on a.suppId = c.fid LEFT JOIN t_settlement_method d on a.settId = d.fid LEFT JOIN t_department e on a.depaId = e.fid where a.billNo like '%${data.billNo}%' and b.custItemCode like '%${data.mid.custItemCode}%' and b.custItemModel like '%${data.mid.custItemModel}%' and b.itemCode like '%${data.mid.itemCode}%' and b.itemModel like '%${data.mid.itemModel}%'
and a.suppId = #{data.suppId}
and a.depaId = #{data.depaId}
and a.billStaf = #{data.billStaf}
and a.billDate between #{date_start} and #{date_end}
